tinymce.PluginManager.add('helpref', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('helpref', {
        text: false,
        icon: 'link',
        onclick: function()
         {
            var x = (tinyMCE.activeEditor.selection.getContent({format : 'text'}));
            x = "<a href='help_searchAllTopics.php?srchLink=" + x + "'>" + x + "</a>";
            tinyMCE.activeEditor.selection.setContent(x);
         }
    });
});